INSERT INTO `blog` (`ID`, `Title`, `ShortDescription`, `Content`, `ImageUrl`, `BlogStatus`, `UserID`, `CategoryID`, `CreatedAt`, `UpdatedAt`) VALUES
(11, 'CĐV Campuchia: \"Về nhà thôi, U22 Việt Nam quá mạnh\"', 'Những cổ động viên Campuchia \"tâm phục khẩu phục\" sau thất bại 0-4 của đội nhà trước U22 Việt Nam ở trận bán kết SEA Games 30 diễn ra tối 7/12.', '<p><strong>Những cổ động vi&ecirc;n Campuchia &quot;t&acirc;m phục khẩu phục&quot; sau thất bại 0-4 của đội nh&agrave; trước U22 Việt Nam ở trận b&aacute;n kết SEA Games 30 diễn ra tối 7/12.</strong></p>\r\n\r\n<p>Trang mạng x&atilde; hội của Li&ecirc;n đo&agrave;n B&oacute;ng đ&aacute; Campuchia đăng kết quả trận thua 0-4 của đội nh&agrave; trước U22 Việt Nam tại b&aacute;n kết&nbsp;<a href=\"https://news.zing.vn/tieu-diem/sea-games-2019.html\" title=\"Tin tức SEA Games 2019\">SEA Games 2019</a>&nbsp;v&agrave; lập tức nhận được nhiều tương t&aacute;c từ người h&acirc;m mộ.</p>\r\n\r\n<p>&quot;Về nh&agrave; th&ocirc;i, U22 Việt Nam qu&aacute; mạnh. Họ nhanh, khỏe, giữ b&oacute;ng tốt. Mất b&oacute;ng một lần l&agrave; thua lu&ocirc;n&quot;, CĐV Chan Raksmey b&igrave;nh luận.</p>\r\n\r\n<p>&quot;Phải thừa nhận l&agrave; họ qu&aacute; mạnh. Ch&uacute;ng ta kh&ocirc;ng c&oacute; cơ hội n&agrave;o. H&atilde;y cố gắng c&oacute; được huy chương&quot;, CĐV Rin Lihang khẳng định. &quot;Ch&uacute;ng ta thua to&agrave;n diện trước U22 Việt Nam&quot;, CĐV Bun Leap quả quyết.</p>\r\n\r\n<p><img alt=\"\" src=\"/cms/uploads/images/DC_zing.jpg\" style=\"height:455px; width:660px\" /></p>\r\n\r\n<p>Đức Chinh l&agrave; người h&ugrave;ng của U22 Việt Nam với c&uacute; đ&uacute;p v&agrave;o lưới Campuchia. Ảnh:&nbsp;<em>Thuận Thắng.</em></p>\r\n\r\n<p>&quot;U22 Việt Nam gần như kh&ocirc;ng thể bị đ&aacute;nh bại. Họ qu&aacute; tốt ở việc tạo &aacute;p lực trong hiệp một. Campuchia ho&agrave;n to&agrave;n kh&ocirc;ng c&oacute; cửa&quot;, CĐV Sirathom Chatchai.</p>\r\n\r\n<p>Một số CĐV hướng mũi d&ugrave;i tới c&ocirc;ng t&aacute;c tổ chức trọng t&agrave;i. &quot;Ch&uacute;ng ta yếu hơn, nhưng U22 Việt Nam cũng kh&ocirc;ng qu&aacute; mạnh v&agrave; c&ograve;n được sự trợ gi&uacute;p của trọng t&agrave;i&quot;, CĐV Kampaul Angkor nhấn mạnh.</p>\r\n\r\n<p>&quot;Trọng t&agrave;i qu&aacute; khắt khe với ch&uacute;ng ta&quot;, CĐV Obita Tola chia sẻ.</p>\r\n\r\n<p>Dẫu vậy, phần đ&ocirc;ng những CĐV Campuchia đều b&agrave;y tỏ th&aacute;i độ ủng hộ U22 Campuchia d&ugrave; thất trận. &quot;D&ugrave; thắng hay thua, h&atilde;y cổ vũ cho U22 Campuchia. Họ đ&atilde; chơi qu&aacute; tốt&quot;, CĐV Sok Leab n&oacute;i.</p>\r\n\r\n<p>U22 Campuchia kh&ocirc;ng c&oacute; qu&aacute; nhiều cơ hội trước Việt Nam mạnh hơn về bản lĩnh v&agrave; đẳng cấp. Chỉ sau 45 ph&uacute;t, U22 Việt Nam đ&atilde; dẫn 3-0 với c&aacute;c pha lập c&ocirc;ng của Nguyễn Tiến Linh v&agrave; c&uacute; đ&uacute;p của&nbsp;<a href=\"https://news.zing.vn/tieu-diem/ha-duc-chinh.html\">H&agrave; Đức Chinh</a>. Trong hiệp 2, U22 Việt Nam c&oacute; b&agrave;n n&acirc;ng tỷ số l&ecirc;n 4-0 v&agrave;o ph&uacute;t 68.&nbsp;</p>\r\n\r\n<p>Cuối trận, U22 Campuchia c&oacute; cơ hội để ghi b&agrave;n danh dự, nhưng Keo Sokpheng lại kh&ocirc;ng thắng được thủ m&ocirc;n Văn Toản tr&ecirc;n chấm phạt đền.</p>\r\n\r\n<p>U22 Campuchia sẽ đ&aacute; trận tranh huy chương đồng với Myanmar l&uacute;c 15h ng&agrave;y 10/12. L&uacute;c 19h c&ugrave;ng ng&agrave;y, U22 Việt Nam sẽ gặp Indonesia trong trận tranh huy chương v&agrave;ng.</p>\r\n', 'uploads/images/img_1575790716.png', 1, 1, 7, '2019-12-08 07:38:36', '2019-12-08 07:38:36'),
(20, 'Mồ hôi, máu và nước mắt đưa tuyển nữ Việt Nam gặt vàng SEA Games', 'Các cô gái áo đỏ đã chiến đấu bằng tất cả sự quyết tâm, tinh thần quả cảm, bỏ qua nỗi đau về thể xác để mang về tấm HCV thứ 6, lập nên kỷ lục cho bóng đá nữ tại SEA Games.', '<h3><span style=\"font-size:22px\"><strong>120 ph&uacute;t chiến đấu với 200% sức lực</strong></span></h3>\r\n\r\n<p>Đối thủ được đ&aacute;nh gi&aacute; cao hơn nhưng tuyển nữ Việt Nam kh&ocirc;ng ngần ngại đẩy cao đội h&igrave;nh tấn c&ocirc;ng ngay sau tiếng c&ograve;i khai cuộc. Sau 90 ph&uacute;t bất ph&acirc;n thắng bại, c&aacute;c c&ocirc; g&aacute;i phải bước v&agrave;o loạt hiệp phụ để ph&acirc;n định thắng thua. Ph&uacute;t 92, tuyển nữ Việt Nam bất ngờ c&oacute; b&agrave;n thắng mở tỷ số của Hải Yến sau một pha b&oacute;ng cố định. Tinh thần của c&aacute;c cầu thủ &aacute;o đỏ d&acirc;ng cao sau khi c&oacute; b&agrave;n thắng. Đo&agrave;n qu&acirc;n của HLV Mai Đức Chung c&oacute; dấu hiệu xuống sức, thậm ch&iacute; c&oacute; l&uacute;c bị chuột r&uacute;t nhưng vẫn nỗ lực bảo vệ khung th&agrave;nh cho đến khi tiếng c&ograve;i m&atilde;n cuộc vang l&ecirc;n. Trận đấu kh&eacute;p lại với thắng lợi 1-0 d&agrave;nh cho thầy tr&ograve; HLV Mai Đức Chung. Tuyển nữ Việt Nam ch&iacute;nh thức bảo vệ th&agrave;nh c&ocirc;ng tấm huy chương v&agrave;ng b&oacute;ng đ&aacute; SEA Games.</p>\r\n\r\n<p><img alt=\"\" src=\"/cms/uploads/images/vietnam_thai_lan_zing_4.jpg\" style=\"height:233px; width:350px\" />&nbsp;&nbsp;<img alt=\"\" src=\"/cms/uploads/images/vietnam_thai_lan_zing_5.jpg\" style=\"float:left; height:233px; width:350px\" /></p>\r\n\r\n<h3>&nbsp;</h3>\r\n\r\n<h3>&nbsp;</h3>\r\n\r\n<h3><span style=\"font-size:22px\"><strong>Trận chung kết diễn ra&nbsp;với m&aacute;u v&agrave; nước mắt</strong></span></h3>\r\n\r\n<p>Với lợi thế về thể lực, c&aacute;c cầu thủ Th&aacute;i Lan lu&ocirc;n tỏ ra sung sức tr&ecirc;n s&acirc;n cỏ. Trong khi đ&oacute;, kh&ocirc;ng kh&oacute; để nhận ra những bước ch&acirc;n của c&aacute;c c&ocirc; g&aacute;i Việt Nam dần trở n&ecirc;n mỏi mệt khi thời gian c&agrave;ng tr&ocirc;i về những ph&uacute;t cuối. H&igrave;nh ảnh c&aacute;c cầu thủ dự bị chạy ra nắn b&oacute;p ch&acirc;n cho đồng đội m&igrave;nh giữa c&aacute;c hiệp thi đấu, h&igrave;nh ảnh một Chương Thị Kiều phải băng b&oacute; nhiều lớp, n&eacute;n đau chạy miệt m&agrave;i tr&ecirc;n s&acirc;n cỏ hay một đội trưởng Huỳnh Như ki&ecirc;n cường chiến đấu đến tận l&uacute;c đi kh&ocirc;ng vững c&oacute; lẽ sẽ thật kh&oacute; qu&ecirc;n với nhiều người h&acirc;m mộ b&oacute;ng đ&aacute; Việt Nam.</p>\r\n\r\n<p><img alt=\"\" src=\"/cms/uploads/images/vietnam_thai_lan_zing_8.jpg\" style=\"height:594px; width:800px\" /></p>\r\n\r\n<h3><strong><span style=\"font-size:22px\">6 huy chương v&agrave;ng v&agrave; kỷ lục SEA Games</span></strong></h3>\r\n\r\n<p>Tuyển nữ Việt Nam ch&iacute;nh thức trở th&agrave;nh đội tuyển b&oacute;ng đ&aacute; nữ đoạt được nhiều HCV nhất qua c&aacute;c kỳ SEA Games. Đến l&uacute;c n&agrave;y, dường như nỗi đau về thể x&aacute;c đ&atilde; nhường chỗ cho niềm vui, niềm tự h&agrave;o, niềm x&uacute;c động đang lan tỏa. HLV Mai Đức Chung nở nụ cười rạng rỡ, ăn mừng chiến thắng trong v&ograve;ng tay của ban huấn luyện v&agrave; c&aacute;c học tr&ograve;. Trong buổi họp b&aacute;o, &ocirc;ng chia sẻ về nỗi lo lắng khi chứng kiến c&ocirc; học tr&ograve; Chương Thị Kiều bị xước mảng đ&ugrave;i, rất x&oacute;t nhưng vẫn quyết t&acirc;m tiếp tục thi đấu. &quot;Trận đấu diễn ra rất nhanh n&ecirc;n b&aacute;c sĩ phải b&oacute; vội v&agrave;ng, hơi hời hợt. T&ocirc;i đ&atilde; qu&aacute;t b&aacute;c sĩ v&agrave; dặn d&ograve; phải b&oacute; kỹ lại cho Kiều&quot;, &ocirc;ng b&agrave;y tỏ t&igrave;nh cảm của m&igrave;nh như một người bố d&agrave;nh cho c&aacute;c cầu thủ nữ Việt Nam.</p>\r\n\r\n<p><img alt=\"\" src=\"/cms/uploads/images/vietnam_thai_lan_zing_9.jpg\" style=\"height:534px; width:800px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n', 'uploads/images/img_1575862833.png', 1, 1, 7, '2019-12-09 03:40:33', '2019-12-09 07:26:08'),
(21, 'Nhan sắc người đẹp da màu đăng quang Hoa hậu Hoàn vũ thế giới 2019', 'Zozibini Tunzi - cô gái da màu đến từ Nam Phi vừa xuất sắc giành vương miện của Hoa hậu Hoàn vũ thế giới 2019. Hoàng Thùy dừng chân ở top 20.', '<p style=\"text-align:center\"><img alt=\"\" src=\"/cms/uploads/images/hh01.jpg\" style=\"height:436px; width:350px\" />&nbsp; &nbsp;<img alt=\"\" src=\"/cms/uploads/images/hh02.jpg\" style=\"height:438px; width:350px\" /></p>\r\n\r\n<p style=\"text-align:center\">H&ocirc;m 9/12 (giờ Việt Nam), chung kết&nbsp;<a href=\"https://news.zing.vn/tieu-diem/hoa-hau-hoan-vu.html\" title=\"Tin tức Hoa Hậu Hoàn Vũ\">Hoa hậu Ho&agrave;n vũ</a>&nbsp;thế giới 2019 kh&eacute;p lại với chiến thắng thuộc về đại diện Nam Phi - Zozibini Tunzi. &Aacute; hậu 1 l&agrave; Puerto Rico c&ograve;n Mexico trở th&agrave;nh &Aacute; hậu 2. Đại diện Việt Nam l&agrave; Ho&agrave;ng Th&ugrave;y g&acirc;y tiếc nuối khi dừng ch&acirc;n ở top 20.</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"/cms/uploads/images/hh03.jpg\" style=\"height:438px; width:350px\" />&nbsp;&nbsp;<img alt=\"\" src=\"/cms/uploads/images/hh04.jpg\" style=\"height:438px; width:350px\" /></p>\r\n\r\n<p style=\"text-align:center\">Với c&acirc;u hỏi: &quot;Điều g&igrave; quan trọng nhất m&agrave; c&aacute;c c&ocirc; g&aacute;i trẻ n&ecirc;n học hỏi?&quot; trong chung kết, c&ocirc; nhận tr&agrave;ng vỗ tay lớn từ kh&aacute;n giả khi trả lời: &quot;Ch&uacute;ng ta l&agrave; những người mạnh mẽ nhất thế giới. T&ocirc;i cho rằng điều quan trọng nhất l&agrave; dạy cho những đứa trẻ rằng ch&uacute;ng cần t&igrave;m kiếm được chỗ đứng của m&igrave;nh trong x&atilde; hội&quot;.</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"/cms/uploads/images/hh05.jpg\" style=\"height:599px; width:800px\" /></p>\r\n\r\n<p style=\"text-align:center\">Trong suốt qu&aacute; tr&igrave;nh dự thi,&nbsp;Zozibini Tunzi thuộc nh&oacute;m th&iacute; sinh nổi trội về ngoại h&igrave;nh với m&aacute;i t&oacute;c t&eacute;m c&aacute; t&iacute;nh, kiến thức rộng v&agrave; khả năng giao tiếp tiếng Anh tốt. Trước đ&oacute;,&nbsp;<em>Missosology</em>&nbsp;dự đo&aacute;n người đẹp da m&agrave;u chắc suất v&agrave;o top 10.&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"/cms/uploads/images/hh06.jpg\" style=\"height:438px; width:350px\" />&nbsp;&nbsp;<img alt=\"\" src=\"/cms/uploads/images/hh07.jpg\" style=\"height:438px; width:350px\" /></p>\r\n\r\n<p style=\"text-align:center\">Zozibini Tunzi sinh năm 1993, cao 1,78 m v&agrave; sở hữu l&agrave;n da n&acirc;u b&oacute;ng gi&ograve;n, gương mặt hiền l&agrave;nh nhưng được đ&aacute;nh gi&aacute; quyến rũ, c&oacute; sức h&uacute;t. Hiện, c&ocirc; đang ho&agrave;n th&agrave;nh chương tr&igrave;nh cử nh&acirc;n của ng&agrave;nh Quan hệ C&ocirc;ng ch&uacute;ng, Đại học Cape Town.&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"/cms/uploads/images/hh08.jpg\" style=\"height:1000px; width:800px\" /></p>\r\n\r\n<p style=\"text-align:center\">&ldquo;T&ocirc;i l&agrave; người tin tưởng vững chắc v&agrave;o gi&aacute;o dục v&agrave; t&ocirc;i sẽ khuy&ecirc;n họ n&ecirc;n đẩy mạnh gi&aacute;o dục. Ch&uacute;ng t&ocirc;i kh&ocirc;ng thể l&agrave;m được g&igrave; nhiều cho người lớn tuổi, nhưng ch&uacute;ng t&ocirc;i sẽ gi&uacute;p đỡ trẻ em bằng c&aacute;ch bắt đầu dạy dỗ ch&uacute;ng sớm&rdquo;, c&ocirc; chia sẻ quan điểm c&aacute; nh&acirc;n.</p>\r\n', 'uploads/images/img_1575864733.png', 1, 1, 9, '2019-12-09 04:12:13', '2019-12-09 07:42:20'),
(22, 'Trần Kiều Ân đáp trả gay gắt khi bị phản đối hẹn hò thiếu gia', 'Trần Kiều Ân công khai hẹn hò họa sĩ giàu có người Malaysia, tuy nhiên tình cảm của cô bị fan phản đối. Nữ diễn viên tỏ ra không quan tâm.', '<p>Ng&agrave;y 8/12, trang&nbsp;<em>QQ</em>&nbsp;đưa tin nữ diễn vi&ecirc;n&nbsp;<a href=\"https://news.zing.vn/tieu-diem/tran-kieu-an.html\">Trần Kiều &Acirc;n</a>&nbsp;mới đ&acirc;y đ&atilde; t&igrave;m thấy bạn trai th&ocirc;ng qua chương tr&igrave;nh T&igrave;nh y&ecirc;u của con g&aacute;i 2. Đối tượng của c&ocirc; l&agrave; một họa sĩ, doanh nh&acirc;n người Malaysia t&ecirc;n l&agrave; Alan, k&eacute;m Trần Kiều &Acirc;n 9 tuổi.</p>\r\n\r\n<p>Trong show, Alan thể hiện m&igrave;nh l&agrave; người sống ch&acirc;n thật, hết l&ograve;ng quan t&acirc;m bạn g&aacute;i. Th&ecirc;m nữa, anh c&ograve;n sở hữu sự nghiệp th&agrave;nh c&ocirc;ng v&agrave; khối t&agrave;i sản lớn. Người h&acirc;m mộ của nữ diễn vi&ecirc;n vẫn cho rằng Alan kh&ocirc;ng xứng với thần tượng của m&igrave;nh.</p>\r\n\r\n<p><img alt=\"\" src=\"/cms/uploads/images/ka1.jpg\" style=\"height:446px; width:720px\" /></p>\r\n\r\n<p>Fan của Trần Kiều &Acirc;n đ&atilde; t&igrave;m một số bằng chứng cho thấy Alan n&oacute;i dối, l&agrave; người c&oacute; cuộc sống ri&ecirc;ng tư hỗn loạn, quan hệ với g&aacute;i mại d&acirc;m v&agrave; vũ nữ tho&aacute;t y. Alan sau đ&oacute; đ&atilde; phải l&ecirc;n tiếng giải th&iacute;ch, Trần Kiều &Acirc;n cũng hỗ trợ chia sẻ lại b&agrave;i viết để trấn an người h&acirc;m mộ. Tuy nhi&ecirc;n, fan của c&ocirc; vẫn bỏ đi v&agrave; c&ograve;n chỉ tr&iacute;ch nữ diễn vi&ecirc;n.</p>\r\n\r\n<p>Ng&agrave;y 8/12, Trần Kiều &Acirc;n đ&atilde; v&agrave;o nh&oacute;m d&agrave;nh ri&ecirc;ng cho fan của m&igrave;nh v&agrave; để lại lời nhắn: &quot;T&ocirc;i kh&ocirc;ng cưỡng &eacute;p ai phải ủng hộ t&igrave;nh y&ecirc;u của m&igrave;nh. C&aacute;c bạn c&oacute; thể tiếp nhận hoặc kh&ocirc;ng đồng t&igrave;nh, đều được, h&atilde;y t&ocirc;n trọng lẫn nhau, đ&oacute; mới l&agrave; phương thức tốt nhất&quot;.</p>\r\n\r\n<p>Một số người cho rằng fan của Trần Kiều &Acirc;n đ&atilde; xen v&agrave;o việc đời tư của thần tượng. Người h&acirc;m mộ chỉ n&ecirc;n ủng hộ t&aacute;c phẩm của nghệ sĩ, những mối quan hệ ngo&agrave;i đời thực chỉ c&oacute; nghệ sĩ mới l&agrave; người hiểu r&otilde; nhất. Một số &yacute; kiến lại cho rằng fan chỉ muốn Trần Kiều &Acirc;n kh&ocirc;ng gặp phải người xấu, kh&ocirc;ng xứng đ&aacute;ng với c&ocirc;.</p>\r\n\r\n<p><img alt=\"\" src=\"/cms/uploads/images/ka2.jpeg\" style=\"height:424px; width:720px\" /></p>\r\n\r\n<p>Trước đ&oacute;, nam diễn vi&ecirc;n Hoắc Kiến Hoa cũng bị fan phản đối rầm rộ khi quyết định cưới nữ diễn vi&ecirc;n lớn tuổi hơn L&acirc;m T&acirc;m Như. Người h&acirc;m mộ của Trịnh Sảng cũng mong c&ocirc; sớm chia tay với bạn trai Trương Hằng v&igrave; cho rằng anh n&agrave;y ăn b&aacute;m c&ocirc;.</p>\r\n', 'uploads/images/img_1575865175.png', 1, 1, 9, '2019-12-09 04:19:35', '2019-12-09 04:19:35'),
(23, 'Học Samsung, Apple định một năm ra iPhone 2 lần', 'Theo JP Morgan, Apple sẽ áp dụng chiến lược ra mắt sản phẩm mới từ năm 2021 với mẫu iPhone 13.', '<p>Trong gần 10 năm,&nbsp;<a href=\"https://news.zing.vn/tieu-diem/cong-ty-apple.html\" title=\"Tin tức Apple\">Apple</a>&nbsp;giữ truyền thống tung c&aacute;c mẫu&nbsp;<a href=\"https://news.zing.vn/tieu-diem/iphone.html\" title=\"Tin tức iPhone\">iPhone</a>&nbsp;mới tại một sự kiện v&agrave;o m&ugrave;a thu (th&aacute;ng 9 hoặc 10) v&agrave; b&aacute;n ra sau đ&oacute; khoảng một th&aacute;ng. Tuy nhi&ecirc;n, theo b&aacute;o c&aacute;o mới từ JP Morgan, h&atilde;ng c&oacute; thể thay đổi chiến lược từ năm 2021.</p>\r\n\r\n<p>Theo nh&agrave; ph&acirc;n t&iacute;ch Samik Chatterjee, Apple sẽ c&oacute; 2 m&agrave;n ra mắt iPhone mỗi năm: một v&agrave;o nửa đầu năm (c&oacute; thể l&agrave; m&ugrave;a xu&acirc;n), c&ograve;n một v&agrave;o nửa cuối (m&ugrave;a thu).</p>\r\n\r\n<p><img alt=\"\" src=\"/cms/uploads/images/ss1.jpg\" style=\"height:405px; width:720px\" /></p>\r\n\r\n<p>Chiến lược n&agrave;y thực ra kh&ocirc;ng mới v&agrave; được nhiều h&atilde;ng Android &aacute;p dụng, phổ biến nhất ch&iacute;nh l&agrave; đối thủ&nbsp;<a href=\"https://news.zing.vn/tieu-diem/cong-ty-samsung.html\" title=\"Tin tức Samsung\">Samsung</a>. H&atilde;ng thường ra mắt d&ograve;ng Galaxy S v&agrave;o đầu năm v&agrave; d&ograve;ng Note v&agrave;o cuối năm. Huawei l&agrave;m điều tương tự với d&ograve;ng P v&agrave; Mate.</p>\r\n\r\n<p>Nếu ch&iacute;nh thức được &aacute;p dụng, đ&acirc;y sẽ l&agrave; lần đầu ti&ecirc;n Apple c&oacute; 2 sự kiện ra mắt iPhone mỗi năm. V&agrave;o 2016, c&ocirc;ng ty n&agrave;y ra mắt mẫu iPhone SE v&agrave;o th&aacute;ng 4. Tuy nhi&ecirc;n, kh&ocirc;ng c&oacute; sự kiện n&agrave;o được tổ chức. H&atilde;ng chỉ đơn thuần mở b&aacute;n sản phẩm n&agrave;y.</p>\r\n\r\n<p>Nếu chia th&agrave;nh 2 sự kiện, Apple c&oacute; thể sẽ giới thiệu mẫu iPhone 13 v&agrave;o đầu năm. Sau đ&oacute;, phi&ecirc;n bản Pro sẽ tr&igrave;nh l&agrave;ng v&agrave;o cuối năm. Cũng c&oacute; thể Apple sẽ tung phi&ecirc;n bản iPhone mới v&agrave;o đầu năm, sau đ&oacute; n&acirc;ng cấp ch&uacute;ng bằng bản &ldquo;S&rdquo; dịp cuối năm, giống c&aacute;ch l&agrave;m của OnePlus với d&ograve;ng T.</p>\r\n\r\n<p>D&ugrave; sử dụng c&aacute;ch n&agrave;o, n&oacute; vẫn c&oacute; &yacute; nghĩa đặc biệt với T&aacute;o khuyết. H&atilde;ng c&oacute; thể b&aacute;n được nhiều m&aacute;y hơn, mở rộng doanh thu vốn đang eo hẹp dần cho nhu cầu thị trường đi xuống. Một điểm quan trọng nữa, chuỗi cung ứng của Apple sẽ &ldquo;dễ thở&rdquo; hơn khi đơn h&agrave;ng của họ được chia đều trong năm, thay v&igrave; dồn v&agrave;o một khoảng thời gian nhất định.</p>\r\n\r\n<p>N&oacute; cũng khiến người d&ugrave;ng dễ lựa chọn hơn, thay v&igrave; bối rối v&igrave; qu&aacute; nhiều mẫu iPhone ra mắt c&ugrave;ng l&uacute;c như hiện tại. Chẳng hạn, năm nay h&atilde;ng tung 3 mẫu iPhone mới gồm iPhone 11, 11 Pro v&agrave; 11 Pro Max với gi&aacute; lần lượt 699, 999 v&agrave;&nbsp;1.099 USD.</p>\r\n', 'uploads/images/img_1575865455.png', 1, 1, 8, '2019-12-09 04:24:16', '2019-12-09 04:24:16'),
(24, 'Xiaomi mở cả dịch vụ cho vay tiền tại Ấn Độ', 'Dịch vụ Mi Credit sẽ kết nối người dùng smartphone của Xiaomi với những công ty cho vay và có thể cung cấp các khoản vay nhanh lên tới 100.000 rupee (khoảng 1.400 USD).', '<p>Theo&nbsp;<em>SCMP,</em>&nbsp;nh&agrave; sản xuất smartphone Trung Quốc&nbsp;<a href=\"https://news.zing.vn/tieu-diem/xiaomi.html\" title=\"Tin tức Xiaomi\">Xiaomi</a>&nbsp;đ&atilde; ra mắt dịch vụ cho vay trực tuyến tại thị trường Ấn Độ.</p>\r\n\r\n<p>Đ&acirc;y được xem l&agrave; bước đi nhằm mở rộng việc cung cấp c&aacute;c sản phẩm t&agrave;i ch&iacute;nh của Xiaomi tại một trong những thị trường dịch vụ trực tuyến lớn nhất thế giới.</p>\r\n\r\n<p>Người đứng đầu chi nh&aacute;nh Xiaomi tại Ấn Độ, Manu Jain cho biết dịch vụ Mi Credit sẽ kết nối người d&ugrave;ng smartphone của h&atilde;ng với c&aacute;c c&ocirc;ng ty cho vay. Mi Credit c&oacute; thể cung cấp c&aacute;c khoản vay nhanh l&ecirc;n tới 100.000 rupee (khoảng&nbsp;1.400 USD).</p>\r\n\r\n<p><img alt=\"\" src=\"/cms/uploads/images/xiaomi2.jpg\" style=\"height:457px; width:720px\" /></p>\r\n\r\n<p>Th&ecirc;m v&agrave;o đ&oacute;, &ocirc;ng Jain cũng t&igrave;m c&aacute;ch xoa dịu những lo ngại về quyền ri&ecirc;ng tư của người d&ugrave;ng Mi Credit tại một quốc gia chưa đưa ra luật về bảo vệ dữ liệu. Theo &ocirc;ng, dữ liệu người d&ugrave;ng được m&atilde; h&oacute;a 100% v&agrave; kh&ocirc;ng thể bị r&ograve; rỉ.</p>\r\n\r\n<p>&ldquo;Ch&uacute;ng t&ocirc;i c&oacute; thỏa thuận bảo vệ dữ liệu với từng đối t&aacute;c của m&igrave;nh rằng họ kh&ocirc;ng thể sử dụng dữ liệu n&agrave;y cho bất kỳ mục đ&iacute;ch n&agrave;o kh&aacute;c, ngo&agrave;i việc cho vay đối với người d&ugrave;ng&rdquo;, &ocirc;ng Jain n&oacute;i.</p>\r\n\r\n<p>Mi Credit l&agrave; sự hợp t&aacute;c của ph&iacute;a Xiaomi với 5 ng&acirc;n h&agrave;ng trong b&oacute;ng tối (shadow banking) c&ugrave;ng một số c&ocirc;ng ty c&ocirc;ng nghệ t&agrave;i ch&iacute;nh (fintech) để đ&aacute;nh gi&aacute; uy t&iacute;n kh&aacute;ch h&agrave;ng v&agrave; giải ng&acirc;n cho c&aacute;c khoản vay.</p>\r\n\r\n<p>Mi Credit kh&ocirc;ng phải l&agrave; bước đi đột ph&aacute; đầu ti&ecirc;n của Xiaomi v&agrave;o lĩnh vực t&agrave;i ch&iacute;nh tại Ấn Độ. Đầu năm nay, nh&agrave; sản xuất smartphone Trung Quốc đ&atilde; giới thiệu ứng dụng thanh to&aacute;n điện tử ngang h&agrave;ng dựa tr&ecirc;n UPI - giao diện thanh to&aacute;n hợp nhất của Ấn Độ.</p>\r\n\r\n<p>Ngo&agrave;i việc kinh doanh smartphone, Xiaomi hiện c&ograve;n đang hướng tới thị trường c&aacute;c thiết bị điện tử c&oacute; gi&aacute; cả phải chăng v&agrave; một số dịch vụ đem về lợi nhuận l&acirc;u d&agrave;i.</p>\r\n\r\n<p>Theo dự b&aacute;o của c&ocirc;ng ty nghi&ecirc;n cứu Boston Consulting Group, v&agrave;o năm 2023, thị trường cho vay trực tuyến tại Ấn Độ c&oacute; thể đạt trị gi&aacute; hơn&nbsp;1.000 tỷ USD.</p>\r\n\r\n<p>&ldquo;Hệ sinh th&aacute;i&rdquo; của ng&agrave;nh c&ocirc;ng nghệ t&agrave;i ch&iacute;nh tại Ấn Độ đang thu h&uacute;t ng&agrave;y c&agrave;ng nhiều c&ocirc;ng ty tham gia v&agrave;o lĩnh vực n&agrave;y. Inc42 dự đo&aacute;n, lĩnh vực c&ocirc;ng nghệ t&agrave;i ch&iacute;nh tại Ấn Độ sẽ đạt mốc&nbsp;1,41 ngh&igrave;n tỷ USD&nbsp;v&agrave;o năm 2022.</p>\r\n\r\n<p>Một số nền tảng thanh to&aacute;n h&agrave;ng đầu đ&atilde; giới thiệu khoản vay c&aacute; nh&acirc;n của họ trong năm ngo&aacute;i, điển h&igrave;nh l&agrave; Paytm- nền tảng được Alibaba v&agrave; Softbank hậu thuẫn.</p>\r\n\r\n<p>&ldquo;Hiện tại, ngo&agrave;i Trung Quốc, Ấn Độ chắc chắn l&agrave; thị trường lớn nhất của ch&uacute;ng t&ocirc;i kh&ocirc;ng chỉ về mảng cho vay t&iacute;n dụng m&agrave; c&ograve;n kh&aacute; nhiều thứ kh&aacute;c. Ch&uacute;ng t&ocirc;i cần mang về th&ecirc;m nhiều nhiều đối t&aacute;c, sản phẩm v&agrave; người d&ugrave;ng hơn để thực sự mở rộng quy m&ocirc; của n&oacute;&rdquo;, Hong Feng - đồng s&aacute;ng lập v&agrave; ph&oacute; chủ tịch cấp cao của Xiaomi cho biết.</p>\r\n', 'uploads/images/img_1575865581.png', 1, 1, 8, '2019-12-09 04:26:21', '2019-12-09 04:26:21'),
(25, 'Đôi giày hoa cúc của G-Dragon hiện có giá lên tới 500 triệu đồng?', 'Giá của từng phiên bản Nike Air Force 1 x PEACEMINUSONE Para-noise dao động tùy theo độ hiếm, có đôi hiện được rao bán tới 500 triệu đồng.', '<p><img alt=\"\" src=\"/cms/uploads/images/dg2.jpg\" style=\"height:720px; width:720px\" /></p>\r\n\r\n<p>D&ugrave; đ&atilde; ra mắt được một thời gian, độ hot của Nike Air Force 1 x PEACEMINUSONE Para-noise vẫn chưa c&oacute; dấu hiệu giảm nhiệt. Kh&ocirc;ng chỉ người h&acirc;m mộ G-Dragon v&agrave; hội &quot;đầu gi&agrave;y&quot;, c&aacute;c ng&ocirc;i sao nổi tiếng cũng mong muốn sở hữu thiết kế n&agrave;y. Trong ng&agrave;y mở b&aacute;n, đ&ocirc;i sneakers b&aacute;n hết chỉ trong v&ograve;ng v&agrave;i ph&uacute;t ở nhiều nơi tr&ecirc;n thế giới như Trung Quốc, Malaysia, Philippines... Ch&iacute;nh những điều n&agrave;y đ&atilde; khiến gi&aacute; của Para-noise bị độn l&ecirc;n kh&aacute; cao</p>\r\n\r\n<p><img alt=\"\" src=\"/cms/uploads/images/dg1.jpg\" style=\"height:720px; width:720px\" /></p>\r\n\r\n<p>Đ&ocirc;i Para-noise phi&ecirc;n bản quốc tế mang dấu swoosh c&oacute; gi&aacute; b&aacute;n ch&iacute;nh thức v&agrave;o khoảng 5,6 triệu đồng. Tuy nhi&ecirc;n hiện tại, bạn phải bỏ ra khoảng hơn 10 triệu đồng để sở hữu item n&agrave;y. Tr&ecirc;n trang mua b&aacute;n gi&agrave;y&nbsp;<em>StockX</em>, thiết kế được &quot;mua đi b&aacute;n lại&quot; với gi&aacute; l&ecirc;n đến&nbsp;656 USD. Theo một số d&acirc;n bu&ocirc;n, mọi người n&ecirc;n mua c&agrave;ng sớm c&agrave;ng tốt, trước khi số lượng h&agrave;ng trở n&ecirc;n khan hiếm v&agrave; gi&aacute; sẽ độn l&ecirc;n cao hơn nữa</p>\r\n\r\n<p><img alt=\"\" src=\"/cms/uploads/images/dg3.jpg\" style=\"height:719px; width:720px\" /></p>\r\n\r\n<p>Ngo&agrave;i phi&ecirc;n bản quốc tế với dấu swoosh trắng, G-Dragon c&ograve;n thiết kế ri&ecirc;ng phi&ecirc;n bản dấu swoosh đỏ cho thị trường H&agrave;n Quốc. Để mua được đ&ocirc;i gi&agrave;y n&agrave;y bạn phải c&oacute; số điện thoại, hộ khẩu H&agrave;n Quốc. So với phi&ecirc;n bản b&igrave;nh thường, item mang sắc đỏ hiếm v&agrave; c&oacute; gi&aacute; trị cao hơn nhiều. Theo&nbsp;<em>Esquire Korea</em>, đ&acirc;y ch&iacute;nh l&agrave; đ&ocirc;i sneakers c&oacute; gi&aacute; b&aacute;n lại cao nhất năm 2019. Ảnh:<em>AAK</em>.&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"/cms/uploads/images/dg4.jpg\" style=\"height:660px; width:660px\" /></p>\r\n\r\n<p>Đ&ocirc;i gi&agrave;y Para-noise c&oacute; dấu swoosh đỏ c&oacute; gi&aacute; l&ecirc;n kệ l&agrave; 219.000 won (tương đương 4,2 triệu đồng). Nhiều bạn trẻ H&agrave;n thậm ch&iacute; c&ograve;n phải xếp h&agrave;ng d&agrave;i ngo&agrave;i trời để mong c&oacute; được cơ hội mua gi&agrave;y. V&igrave; thế, kh&ocirc;ng qu&aacute; kh&oacute; hiểu khi đ&ocirc;i sneakers được rao b&aacute;n lại với gi&aacute; gấp 20 lần. Ở Việt Nam, nếu muốn sở hữu item n&agrave;y, kh&aacute;ch h&agrave;ng sẽ phải chi ra tới 100 triệu đồng.</p>\r\n', 'uploads/images/img_1575865818.png', 1, 1, 10, '2019-12-09 04:30:18', '2019-12-09 04:30:18'),
(26, 'Cổ Lực Na Trát chuộng diện váy bó sát, khoe vòng một khi dự sự kiện', 'Mỹ nhân Tân Cương thường được khen trông như nữ thần khi lựa chọn trang phục gợi cảm, bắt mắt xuất hiện trước công chúng.', '<p><img alt=\"\" src=\"/cms/uploads/images/tt1.jpg\" style=\"height:1019px; width:720px\" /></p>\r\n\r\n<p>Cổ Lực Na Tr&aacute;t mới đ&acirc;y trở th&agrave;nh t&acirc;m điểm ch&uacute; &yacute; khi xuất hiện tại lễ trao giải&nbsp;<em>Mnet Asian Music Awards 2019&nbsp;</em>diễn ra tại Nhật Bản. B&ecirc;n cạnh nhan sắc xinh đẹp, c&ocirc; nhận về nhiều b&igrave;nh luận ti&ecirc;u cực với m&agrave;n n&oacute;i tiếng Anh, l&ecirc;n trao giải cho nghệ sĩ xuất sắc. Clip ghi lại cảnh n&agrave;y được chia sẻ li&ecirc;n tục tr&ecirc;n c&aacute;c diễn đ&agrave;n, thậm ch&iacute; bị đ&aacute;nh gi&aacute; l&agrave; thảm hoạ</p>\r\n\r\n<p><img alt=\"\" src=\"/cms/uploads/images/tt2.jpg\" style=\"height:496px; width:350px\" /><img alt=\"\" src=\"/cms/uploads/images/tt3.jpg\" style=\"height:504px; width:350px\" /></p>\r\n\r\n<p>Trước khi g&acirc;y tranh c&atilde;i, Cổ Lực Na Tr&aacute;t được biết đến như một trong những biểu tượng sắc đẹp tại Trung Quốc. 9X lu&ocirc;n được mời tham dự c&aacute;c sự kiện lớn, đặc biệt l&agrave; li&ecirc;n quan đến lĩnh vực thời trang, l&agrave;m đẹp.</p>\r\n\r\n<p><img alt=\"\" src=\"/cms/uploads/images/tt4.jpg\" style=\"height:470px; width:350px\" /><img alt=\"\" src=\"/cms/uploads/images/tt5.jpg\" style=\"height:443px; width:350px\" /></p>\r\n\r\n<p>Trước c&ocirc;ng ch&uacute;ng, nữ diễn vi&ecirc;n thường xuất hiện với h&igrave;nh ảnh thu h&uacute;t, lộng lẫy nhất. C&oacute; thể thấy 9X ưa chuộng trang phục đ&iacute;nh đ&aacute;, sequin lấp l&aacute;nh gi&uacute;p c&ocirc; nổi bật ở sự kiện</p>\r\n\r\n<p><img alt=\"\" src=\"/cms/uploads/images/tt6.jpg\" style=\"height:498px; width:350px\" /><img alt=\"\" src=\"/cms/uploads/images/tt7.jpg\" style=\"height:483px; width:350px\" /></p>\r\n\r\n<p>Những mẫu v&aacute;y &aacute;o mỹ nh&acirc;n T&acirc;n Cương diện thường c&oacute; phom d&aacute;ng b&oacute; s&aacute;t, c&uacute;p ngực gợi cảm gi&uacute;p t&ocirc;n l&ecirc;n v&oacute;c d&aacute;ng chuẩn của 9X. C&oacute; thể thấy c&aacute;c mẫu trang phục n&agrave;y kh&aacute; k&eacute;n d&aacute;ng nhưng kh&ocirc;ng thể &quot;l&agrave;m kh&oacute;&quot; c&ocirc; n&agrave;ng</p>\r\n', 'uploads/images/img_1575866137.png', 1, 1, 10, '2019-12-09 04:35:37', '2019-12-09 04:35:37'),
(27, 'HLV Park: \"Tôi mơ 2 đội tuyển cùng mang HCV trở về\"', 'Sáng 9/12, huấn luyện viên Park Hang-seo khẳng định ông cùng các cầu thủ U22 Việt Nam quyết tâm đánh bại Indonesia để giành HCV môn bóng đá nam SEA Games 30.', '<p>Buổi họp b&aacute;o diễn ra l&uacute;c 11h15 v&agrave; thu h&uacute;t rất đ&ocirc;ng truyền th&ocirc;ng. &Ocirc;ng Park chia sẻ với b&aacute;o ch&iacute; ngay sau đồng nghiệp b&ecirc;n ph&iacute;a Indonesia. Vị thuyền trưởng U22 Việt Nam mở đầu: &quot;B&oacute;ng đ&aacute; Việt Nam đ&atilde; c&oacute; nhiều lần thử lửa nhưng chưa th&agrave;nh c&ocirc;ng. Lần n&agrave;y v&agrave; l&uacute;c n&agrave;y, tất cả chuẩn bị cho khoảnh khắc gi&agrave;nh huy chương v&agrave;ng SEA Games&quot;.</p>\r\n\r\n<p><img alt=\"\" src=\"/cms/uploads/images/pk1.jpg\" style=\"height:424px; width:720px\" /></p>\r\n\r\n<p>&quot;T&ocirc;i l&agrave; huấn luyện vi&ecirc;n v&agrave; đ&atilde; trải qua nhiều giải đấu quốc tế nhưng l&agrave;m ở Việt Nam rất đặc biệt. T&ocirc;i y&ecirc;u Việt Nam rất nhiều v&agrave; muốn mang danh hiệu n&agrave;y cho người h&acirc;m mộ như tr&aacute;ch nhiệm của m&igrave;nh phải thực hiện&quot;, HLV Park Hang-seo khẳng định.</p>\r\n\r\n<p>Trước c&acirc;u hỏi về c&aacute;c huấn luyện vi&ecirc;n ch&acirc;u &Aacute;, &ocirc;ng Park Hang-seo từ chối nhận x&eacute;t đồng nghiệp v&agrave; chỉ muốn tập trung v&agrave;o c&ocirc;ng việc của m&igrave;nh với c&aacute;c đội tuyển b&oacute;ng đ&aacute; Việt Nam.</p>\r\n\r\n<p>&quot;H&ocirc;m 8/12, ch&uacute;ng t&ocirc;i xem trận chung kết b&oacute;ng đ&aacute; nữ, tự h&agrave;o về c&aacute;c cầu thủ nữ v&agrave; huấn luyện vi&ecirc;n. Họ đ&atilde; thể hiện tinh thần của người d&acirc;n Việt Nam. Ch&uacute;ng t&ocirc;i sẽ rất vui nếu trở về tr&ecirc;n chuyến bay c&ugrave;ng đội tuyển nữ Việt Nam. T&ocirc;i mơ hai đội tuyển trở về với HCV SEA Games 30&quot;, HLV Park n&oacute;i.</p>\r\n\r\n<p>Nh&agrave; cầm qu&acirc;n người H&agrave;n Quốc cho rằng b&oacute;ng đ&aacute; l&agrave; m&ocirc;n đối kh&aacute;ng kh&oacute; chịu, v&agrave; cầu thủ Việt Nam l&agrave; những chiến binh. &quot;T&ocirc;i kh&ocirc;ng đồng &yacute; với quan điểm cầu thủ Indonesia c&oacute; lối chơi bạo lực. Họ được tổ chức tốt, c&oacute; thể trạng, hiểu r&otilde; vai tr&ograve; chiến thuật. Khả năng chuyển đổi từ ph&ograve;ng ngự sang phản c&ocirc;ng của họ rất nhanh, gần như đ&acirc;y l&agrave; tập thể ho&agrave;n hảo. T&ocirc;i kh&ocirc;ng thể n&oacute;i trước điều g&igrave; l&uacute;c n&agrave;y. Cả hai đội đều ghi 17 b&agrave;n, nhưng Indonesia chủ yếu ghi b&agrave;n từ hai bi&ecirc;n, n&ecirc;n ch&uacute;ng t&ocirc;i biết phải chuẩn bị thế n&agrave;o với họ&quot;, HLV Park nhận định về đối thủ trong trận chung kết.</p>\r\n\r\n<p>&quot;Trận đấu kh&oacute; khăn về mặt thể lực. Cầu thủ Indonesia c&oacute; năng lực đồng đều, nhưng họ cũng c&oacute; điểm yếu v&agrave; ch&uacute;ng t&ocirc;i sẽ khai th&aacute;c điều đ&oacute;. Thật kh&ocirc;ng đ&uacute;ng khi lại n&oacute;i về sai lầm của thủ m&ocirc;n, đừng hỏi t&ocirc;i về điều đ&oacute;. C&aacute;c bạn thấy phong độ của họ thế n&agrave;o, th&igrave; cứ viết l&ecirc;n b&aacute;o. Họ đang hồi phục tốt để chuẩn bị cho trận chung kết&quot;, HLV Park khẳng định.</p>\r\n\r\n<p>Trước khi kết th&uacute;c buổi họp b&aacute;o, vị thuyền trưởng U22 Việt Nam chia sẻ: &quot;H&ugrave;ng Dũng đảm nhận tốt vai tr&ograve; l&atilde;nh đạo. Quang Hải vắng mặt v&igrave; chấn thương, n&ecirc;n ai l&agrave; đội trưởng cũng được v&igrave; ch&uacute;ng t&ocirc;i c&oacute; c&ugrave;ng một mục ti&ecirc;u&quot;.</p>\r\n\r\n<p>Chiều 9/12, U22 Việt Nam tập k&iacute;n để chuẩn bị cho chung kết với Indonesia. Trận tranh HCV m&ocirc;n b&oacute;ng đ&aacute; nam SEA Games 30 sẽ diễn ra l&uacute;c 19h ng&agrave;y 10/12.</p>\r\n', 'uploads/images/img_1575889427.png', 1, 1, 7, '2019-12-09 11:03:47', '2019-12-09 11:03:47'),
(28, 'Nữ tuyển thủ Việt Nam nhập viện cấp cứu vì kiệt sức', 'Sau khi trở về khách sạn, trung vệ Trần Thị Hồng Nhung kiệt sức và được đưa đi bệnh viện cấp cứu.', '<p>Hồng Nhung ra s&acirc;n từ đầu trong trận chung kết b&oacute;ng đ&aacute; nữ tối 8/12. Kh&ocirc;ng chỉ ri&ecirc;ng Hồng Nhung chơi lăn xả, nhiều đồng đội cũng kiệt sức sau khi gi&agrave;nh chiến thắng 1-0 trước Th&aacute;i Lan.</p>\r\n\r\n<p>Hồng Nhung vẫn ăn mừng v&agrave; vui vẻ khi trao giải nhưng về tới kh&aacute;ch sạn, trung vệ n&agrave;y c&oacute; biểu hiện kiệt sức. Nữ tuyển thủ đang kho&aacute;c &aacute;o CLB H&agrave; Nam được ban huấn luyện chuyển ngay đến bệnh viện cấp cứu.</p>\r\n\r\n<p><img alt=\"\" src=\"/cms/uploads/images/tv1.jpg\" style=\"height:540px; width:720px\" /></p>\r\n\r\n<p>Hiện tại, t&igrave;nh h&igrave;nh của Nhung đ&atilde; ổn định. Đại diện Li&ecirc;n đo&agrave;n B&oacute;ng đ&aacute; Việt Nam sang thăm hỏi, động vi&ecirc;n cầu thủ sinh năm 1992. Hồng Nhung l&agrave; một trong những vị tr&iacute; quan trọng với đội h&igrave;nh của HLV Mai Đức Chung.</p>\r\n\r\n<p>Ở CLB H&agrave; Nam, Hồng Nhung cũng l&agrave; trụ cột kh&ocirc;ng thể thay thế. C&ocirc; g&aacute;i sinh năm 1992 chỉ ngất xỉu khi trở về kh&aacute;ch sạn. Nhiều cầu thủ kh&aacute;c thậm ch&iacute; kh&ocirc;ng thể chơi hết 90 ph&uacute;t, kh&ocirc;ng tự đi được cũng v&igrave; kiệt sức.</p>\r\n\r\n<p>Cuối th&aacute;ng 4 năm nay, Hồng Nhung được CLB Chonburi mời sang Th&aacute;i Lan thi đấu giải v&ocirc; địch quốc gia nữ Th&aacute;i Lan. Nữ trung vệ của Việt Nam gi&uacute;p đội b&oacute;ng Th&aacute;i Lan giữ sạch lưới trong suốt 90 ph&uacute;t thi đấu ch&iacute;nh thức v&agrave; gi&agrave;nh chiến thắng tr&ecirc;n chấm lu&acirc;n lưu để bước v&agrave;o chung kết.</p>\r\n\r\n<p>C&aacute;c b&aacute;c sĩ sẽ theo d&otilde;i t&igrave;nh h&igrave;nh sức khỏe của Hồng Nhung trong 2 ng&agrave;y tới. Theo lịch đ&atilde; thay đổi, tuyển nữ Việt Nam sẽ ở lại Manila v&agrave; trở về ng&agrave;y 11/12 sau khi ủng hộ đội U22 Việt Nam đ&aacute; trận chung kết với Indonesia.</p>\r\n\r\n<p>Nếu sức khỏe đảm bảo, Hồng Nhung c&oacute; thể đến s&acirc;n Rizal Memorial để động vi&ecirc;n U22 Việt Nam. Th&agrave;nh t&iacute;ch v&agrave; tinh thần của c&aacute;c nữ tuyển thủ Việt Nam l&agrave; nguồn động lực lớn lao cho thầy tr&ograve; HLV Park Hang-seo.</p>\r\n', 'uploads/images/img_1575891285.png', 1, 1, 7, '2019-12-09 11:34:45', '2019-12-09 11:34:45');

